                .BindAsync<MOption<Task<A>>, Option<Task<A>>, MTask<A>, Task<A>, A>(ma, 
                    async a => (await pred(a))
                        ? default(MTask<A>).ReturnAsync(a.AsTask())
                        : default(MTask<A>).Zero());

