            default(ApplEnumerable< Task<A>, Task<B>>).Apply(
                default(MEnumerable< Func<Task<A>, Task<B>>>).Return(
                    (Task<A> a) => 
                        default(ApplTask< A, B>).Apply(
                            default(MTask< Func<A, B>>).ReturnAsync(fab.AsTask()), 
                            a)),
                fa);

